import React, { Component } from 'react';
import { Link, animateScroll as scroll } from 'react-scroll';
import Header from './Components/Header/Header';
import Education from './Components/Education/Education';
import Projects from './Components/Projects/Projects';
import Contact from './Components/Contact/Contact'
import './App.css';

class App extends Component {

  state = {
    currentPage: null
  }

  /*
  handlePageChange = (requestedPage) => {
    if (requestedPage === 'Education') {
      this.setState({
        currentPage: <Education id='section1'/>
      })
    }else if (requestedPage === 'Projects') {
      this.setState({
        currentPage: <Projects id='section2' />
      })
    }else if (requestedPage === 'Contact') {
      this.setState({
        currentPage: <Contact id='section3' />
      })
    }
  }
*/


  render() {

    return (
      <div>

      <div className='background'>
      <div className='app'>
        <Header handlePageChange={this.handlePageChange} />
        <li >
              <Link
                activeClass="active"
                to='section1'
                spy={true}
                smooth={true}
                offset={-70}
                duration={500}
              >
                Section 1
              </Link>
            </li>
            <li>
              <Link
                activeClass="active"
                to='section2'
                spy={true}
                smooth={true}
                offset={-70}
                duration={500}
              >
                Section 2
              </Link>
            </li>
            <li>
              <Link
                activeClass="active"
                to='section3'
                spy={true}
                smooth={true}
                offset={-70}
                duration={500}
              >
                Section 3
              </Link>
            </li>
        <div className='container'>
        <Education id='section1'/>
        <Projects id='section2' />
        <Contact id='section3' />
        {this.state.currentPage}
        </div>
      </div>
      </div>
      </div>
    );
  }
}

export default App;
