import React, { Component } from 'react';
import { Link, animaleScroll as scroll } from 'react-scroll';
import Header from './Components/Header/Header';
import Education from './Components/Education/Education';
import Projects from './Components/Projects/Projects';
import Contact from './Components/Contact/Contact'
import './App.css';

class App extends Component {

  state = {
    currentPage: null,
    scrollY: window.scrollY
  }

  componentDidMount() {
    window.addEventListener('scroll', this.handleScroll)
  }

  handleScroll = (event) => {
    this.setState({
      scrollY: window.scrollY
    })
    console.log(this.state.scrollY)

  }

  handlePageChange = (requestedPage) => {
    if (requestedPage === 'Education') {
      this.setState({
        currentPage: <Education id='section1'/>
      })
    }else if (requestedPage === 'Projects') {
      this.setState({
        currentPage: <Projects id='section2' />
      })
    }else if (requestedPage === 'Contact') {
      this.setState({
        currentPage: <Contact id='section3' />
      })
    }
  }



  render() {

    if (this.state.scrollY > 10) {
      alert('Scrollin')
    }

    return (
      <div>

      <li >
              <Link
                activeClass="active"
                to="section1"
                spy={true}
                smooth={true}
                offset={-70}
                duration={500}
              >
                Section 1
              </Link>
            </li>
            <li>
              <Link
                activeClass="active"
                to="section2"
                spy={true}
                smooth={true}
                offset={-70}
                duration={500}
              >
                Section 2
              </Link>
            </li>
            <li>
              <Link
                activeClass="active"
                to="section3"
                spy={true}
                smooth={true}
                offset={-70}
                duration={500}
              >
                Section 3
              </Link>
            </li>


      <div className='background'>
      <div className='app'>
        <Header handlePageChange={this.handlePageChange} />
        <Link to="section1">Section 1</Link>
        <div className='container'>
        {this.state.currentPage}
        </div>
      </div>
      </div>
      </div>
    );
  }
}

export default App;
