import React, { Component } from 'react';
import './NavBar.css';

import { Link } from 'react-scroll';

class NavBar extends Component {

    state = {
        activeContainer: 'homeContainer'
    }

    handleContainerChange(newContainer) {
        this.setState({
            activeContainer: newContainer
        })
    }

    render() {

        let navBarClass = 'nav-bar nav-bar-' + this.props.theme
        let linkClass = 'nav-btn nav-btn-' + this.props.theme
        let activeLinkClass = 'nav-btn nav-btn-active-'+this.props.theme




        return (
            <div className={navBarClass}>
                <Link className={linkClass} to='homeContainer'
                    spy={true} smooth={true} duration={500} activeClass={activeLinkClass}
                    onClick={() => this.handleContainerChange('homeContainer')}>Home</Link>
                <Link className={linkClass} to='resumeContainer'
                    spy={true} smooth={true} duration={500} activeClass={activeLinkClass} 
                    onClick={() => this.handleContainerChange('resumeContainer')}>Resume</Link>
                <Link className={linkClass} to='projectsContainer'
                    spy={true} smooth={true} duration={500} activeClass={activeLinkClass} 
                    onClick={() => this.handleContainerChange('projectsContainer')}>Projects</Link>
                <Link className={linkClass} to='contactContainer'
                    spy={true} smooth={true} duration={500} activeClass={activeLinkClass} 
                    onClick={() => this.handleContainerChange('contactContainer')}>Contact</Link>
                <select name='themes' onChange={this.props.handleThemeChange(this.event)}>
                    <option value='dark'>Dark</option>
                    <option value='light'>Light</option>
                </select>
            </div>
        )
    }

}

export default NavBar;
