import React, { Component } from 'react';
import Header from './Components/Header/Header';
import Education from './Components/Education/Education';
import Projects from './Components/Projects/Projects';
import Contact from './Components/Contact/Contact'
import './App.css';


import * as Scroll from 'react-scroll';
import { Link, Element , Events, animateScroll as scroll, scrollSpy, scroller } from 'react-scroll'

class App extends Component {

  state = {
    currentPage: null
  }

  /*
  handlePageChange = (requestedPage) => {
    if (requestedPage === 'Education') {
      this.setState({
        currentPage: <Education id='section1'/>
      })
    }else if (requestedPage === 'Projects') {
      this.setState({
        currentPage: <Projects id='section2' />
      })
    }else if (requestedPage === 'Contact') {
      this.setState({
        currentPage: <Contact id='section3' />
      })
    }
  }
*/

  render() {

    return (
      <div>
        <div className='background'>
        <Header handlePageChange={this.handlePageChange} />
        <Link activeClass="active" to="education" spy={true} smooth={true} offset={50} duration={500} onSetActive={this.handleSetActive}>
          Test 1
        </Link>
          <div className='app'>
            <div className='container'>
              <Education id="education" />
              <Projects id='projects' />
              <Contact id='contact' />
              {this.state.currentPage}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
