import React, { Component } from 'react';
import NavBar from './Components/NavBar/NavBar'
import Education from './Components/Education/Education';
import Projects from './Components/Projects/Projects';
import Contact from './Components/Contact/Contact'
import Home from './Components/Home/Home'
import './App.css';

import { Events } from "react-scroll";


class App extends Component {

  state = {
    currentContainer: 'Home'
  }

    componentDidMount() {
      Events.scrollEvent.register("begin", function(to, element) {
        console.log("begin", arguments);
      });

      Events.scrollEvent.register("end", function(to, element) {
        console.log("end", arguments);
      });
    }

    handleNavClick = (newContainer) => {
      this.setState({
        currentContainer: newContainer
      })
      console.log(newContainer)
    }

  render() {

    let isHomeActive = false;
    let isEducationActive = false;
    let isProjectsActive = false;
    let isContactActive = false;

    if (this.state.currentContainer === 'Home') {
      isHomeContainer = true;
    }


    return (
        <div className='app'>
            <NavBar handleNavClick={() => this.handleNavClick.bind()}/>
            <div className='container'>
              <Home id='homeContainer' />
              <Education id="educationContainer" />
              <Projects id='projectsContainer' />
              <Contact id='contactContainer' />
            </div>
          </div>
    );
  }
}

export default App;
