import React, { Component } from 'react';
import { Link, animateScroll as scroll } from 'react-scroll';
import Header from './Components/Header/Header';
import Education from './Components/Education/Education';
import Projects from './Components/Projects/Projects';
import Contact from './Components/Contact/Contact'
import dummyText from './DummyText'
import Section from './Section'
import './App.css';

class App extends Component {

  state = {
    currentPage: null
  }

  /*
  handlePageChange = (requestedPage) => {
    if (requestedPage === 'Education') {
      this.setState({
        currentPage: <Education id='section1'/>
      })
    }else if (requestedPage === 'Projects') {
      this.setState({
        currentPage: <Projects id='section2' />
      })
    }else if (requestedPage === 'Contact') {
      this.setState({
        currentPage: <Contact id='section3' />
      })
    }
  }
*/

  scrollToTop = () => {
    scroll.scrollToTop();
  };

  render() {

    return (
      <div>
        <div className='background'>
          <div className='app'>
            <Header handlePageChange={this.handlePageChange} />

            <nav className="nav" id="navbar">
        <div className="nav-content">
          <ul className="nav-items">
            <li className="nav-item">
              <Link
                activeClass="active"
                to="section1"
                spy={true}
                smooth={true}
                offset={-70}
                duration={500}
              >
                Section 1
              </Link>
            </li>
            <li className="nav-item">
              <Link
                activeClass="active"
                to="section2"
                spy={true}
                smooth={true}
                offset={-70}
                duration={500}
              >
                Section 2
              </Link>
            </li>
            <li className="nav-item">
              <Link
                activeClass="active"
                to="section3"
                spy={true}
                smooth={true}
                offset={-70}
                duration={500}
              >
                Section 3
              </Link>
            </li>
            <li className="nav-item">
              <Link
                activeClass="active"
                to="section4"
                spy={true}
                smooth={true}
                offset={-70}
                duration={500}
              >
                Section 4
              </Link>
            </li>
            <li className="nav-item">
              <Link
                activeClass="active"
                to="section5"
                spy={true}
                smooth={true}
                offset={-70}
                duration={500}
              >
                Section 5
              </Link>
            </li>
          </ul>
        </div>
      </nav>

      <div className="App">
        <Section
          title="Section 1"
          subtitle={dummyText}
          dark={true}
          id="section1"
        />
        <Section
          title="Section 2"
          subtitle={dummyText}
          dark={false}
          id="section2"
        />
        <Section
          title="Section 3"
          subtitle={dummyText}
          dark={true}
          id="section3"
        />
        <Section
          title="Section 4"
          subtitle={dummyText}
          dark={false}
          id="section4"
        />
        <Section
          title="Section 5"
          subtitle={dummyText}
          dark={true}
          id="section5"
        />
      </div>



            <div className='container'>
              <Education id="section1" />
              <Projects id='section2' />
              <Contact id='section3' />
              {this.state.currentPage}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
